import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer
} from "recharts";

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Fetch stock data from free API (dummy demo)
    axios
      .get("https://api.coindesk.com/v1/bpi/historical/close.json")
      .then((res) => {
        const prices = Object.entries(res.data.bpi).map(([date, price]) => ({
          date,
          price,
        }));
        setData(prices);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div style={{ width: "100%", height: "500px", padding: "20px" }}>
      <h2 style={{ textAlign: "center" }}>Stock Price Analysis (Demo)</h2>
      <ResponsiveContainer>
        <LineChart data={data}>
          <CartesianGrid stroke="#ccc" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip />
          <Line type="monotone" dataKey="price" stroke="#8884d8" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default App;
